#[wasm_bindgen]
pub fn process(a:i32, s:String, b:i32) -> String {
    if s.len() == 0 {
        return "".to_string()
    }
    match hex::decode(s) {
        Ok(request) => {
            match decode(&request) {
                Ok(r) => match r {
                    Some(m) => {
                        let r: pb::DataMap = _process(m, a, b);
                        let rb = encode(CORE_DATA_MAP, &r).unwrap();
                        let reply = hex::encode(rb);
                        return reply;
                    }
                    _ => {
                        return "".to_string()
                    }
                }
                _ => {
                    return "".to_string()
                }
            }
        }
        _ => {
            return "".to_string()
        }
    }
}

#[wasm_bindgen]
pub fn list(a:i32, s:String, b:i32) -> String {
    if s.len() == 0 {
        return "".to_string()
    }
    match hex::decode(s) {
        Ok(request) => {
            match decode(&request) {
                Ok(r) => match r {
                    Some(list) => {
                        let rlist: pb::DataList = _list(list, a, b);
                        let rb = encode(CORE_DATA_LIST, &rlist).unwrap();
                        let reply = hex::encode(rb);
                        return reply;
                    }
                    _ => {
                        return "".to_string()
                    }
                }
                _ => {
                    return "".to_string()
                }
            }
        }
        _ => {
            return "".to_string()
        }
    }
}

#[wasm_bindgen]
pub fn call_winner(input: &str) -> i32 {
    let lines:[[usize; 3]; 8] = [
        [0, 1, 2],
        [3, 4, 5],
        [6, 7, 8],
        [0, 3, 6],
        [1, 4, 7],
        [2, 5, 8],
        [0, 4, 8],
        [2, 4, 6]
    ];
    let split = input.split(",");
    let elements: Vec<&str> = split.collect();
    for i in 0..lines.len() {
        let [a, b, c] = lines[i];
        if elements[a].len() > 0 && elements[a] == elements[b] && elements[a] == elements[c] {
            match elements[a] {
                "X" => return 2,
                "O" => return 1,
                 _  => return 0,
            }
        }
    }
    return 0;
}

